use std::env::{self};
use std::process;

use risc0_zkvm::Receipt;

mod cli_input;
mod nft_utils;
mod termbin_utils;
mod zk_utils;

// @TODO better expain errors here
// @TODO write tests for all utils
// @TODO finish console logs
#[tokio::main]
async fn main() {
    let args: Vec<String> = env::args().collect();
    if args.len() != 2 {
        eprintln!("Wrong number of arguments");
        process::exit(1);
    }
    if args[1] != "prover" && args[1] != "verifier" {
        eprintln!(
            "Wrong execution mode, expected prover or verifier, received {}",
            args[1]
        );
        process::exit(1);
    }
    if args[1] == "prover" {
        let (address, signature) = cli_input::prover_inputs().unwrap_or_else(|e| {
            eprintln!("Error: {}", e);
            process::exit(1);
        });
        let nft_owners = nft_utils::fetch_nft_owners(address)
            .await
            .unwrap_or_else(|e| {
                eprintln!("Error: {}", e);
                process::exit(1);
            });
        let receipt: Receipt = zk_utils::generate_proof(address, signature, nft_owners)
            .unwrap_or_else(|e| {
                eprintln!("Error: {}", e);
                process::exit(1);
            });
        let termbin_link = termbin_utils::upload_receipt_to_termbin(&receipt)
            .await
            .unwrap_or_else(|e| {
                eprintln!("Error: {}", e);
                process::exit(1);
            });

        println!("{:?}", termbin_link);
    }
}
